function [ oArgs ] = MatrixN(psi, teta)N=zeros(6,8);N(1,1) = N(4,1) = 0.25*(1-psi)*(1+teta);N(1,2) = N(4,2) = 0.25*(1+psi)*(1+teta);N(1,3) = N(4,3) = 0.25*(1+psi)*(1-teta);N(1,4) = N(4,4) = 0.25*(1-psi)*(1-teta);N(2,1) = N(5,1) = -0.25*(1+teta);N(2,2) = N(5,2) = 0.25*(1+teta);N(2,3) = N(5,3) = 0.25*(1-teta);N(2,4) = N(5,4) = -0.25*(1-teta);N(3,1) = N(6,1) = 0.25*(1-psi);N(3,2) = N(6,2) = 0.25*(1+psi);N(3,3) = N(6,3) = -0.25*(1+psi);N(3,4) = N(6,4) = -0.25*(1-psi);oArgs = N;end